const initialState = []

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'GET_USER': {
            return [
                ...state, ...action.payload
            ]
        }
        case 'CREATE_USER': {
            return [
                ...state, { ...action.payload }
            ]
        }
        default:
            return state
    }
}

export default userReducer
