const initialState = {}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ALL_USER': {
            return {
                ...state,
                user: action.payload
            }
        }
        case 'CREATE_USER': {
            return {
                ...state,
                user: action.payload
            }
        }
        default:
            return state
    }
}

export default userReducer
